import './App.css'
import {useState} from "react";

function App() {
    const [board, setBoard] = useState([
        {
            id: 1,
            title: 'Сделать',
            items: [{id: 1, title: 'Посадить деревья'}, {id: 2, title: 'Доебаться до Лизы'}, {
                id: 3,
                title: 'Посмотреть аниме'
            }]
        },
        {
            id: 2,
            title: 'Проверить',
            items: [{id: 1, title: 'Контрольная матан'}, {id: 2, title: 'Контрольная по диффурам'}, {
                id: 3,
                title: 'Контрольная ТФКП'
            }]
        },
        {
            id: 3,
            title: 'Выполнено',
            items: [{id: 1, title: 'Дз матан'}, {id: 2, title: 'Дз ТФКП'}, {id: 3, title: 'Числаки'}]
        },
    ]);

    return (
        <div className="App">
            {board.map(board =>
                <div className='board'>
                    <div className="board__title">
                        {board.title}
                    </div>
                    {board.items.map(item =>
                        <div className='item'
                             onDragOver={e => dragHandler(e, )}
                             onDragLeave={e => dragHandler(e, )}
                             onDragStart={e => dragHandler(e, )}
                             onDragEnd={e => dragHandler(e, )}
                             onDrop={e => dragHandler(e, )}
                             draggable={true}
                        >
                            {item.title}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default App;
